mapper.update:
update pt_menu_directory m 
inner join pt_res r on m.menu_id=r.res_uuid 
set r.res_url=#{res_url}, m.dir_code=#{dir_code}, m.dir_name=#{dir_name}, 
m.dir_level_number=#{dir_level_number}, m.parent_id=#{parent_id}, 
m.isitem=#{isitem}, m.dir_order=#{dir_order}
where menu_id =	#{res_uuid}
